mapper.update:
update comment


        status = #{status}


        status = 0




      user_id = #{userId}


      and comment_id = #{commentId}

update comment


        user_id = #{userId}


        aim = #{commentAim}


        comic_id = #{comicId}


        detail_id = #{detailId}


        content = #{content}


        time = #{time}


        status = #{status}


        status = 0




        and comment_id = #{commentId}

mapper.select:
select *
from comment,user
where user.user_id = comment.user_id
  and aim in (select comment_id from comment where user_id=#{userId})
  and comment.status=#{status}
order by time desc;

select *
from comment,user
where user.user_id = comment.user_id and comment_id = #{commentId}
